import React from "react";
import { motion } from "framer-motion";

const App: React.FC = () => {
  return (
    <div className="relative flex items-center justify-center min-h-screen overflow-hidden bg-gradient-to-br from-purple-900 via-pink-600 to-yellow-500 text-white">
      {/* Background pattern */}
      <div className="absolute inset-0 opacity-30 bg-[radial-gradient(circle_at_20%_20%,rgba(255,255,255,0.2),transparent_25%),radial-gradient(circle_at_80%_80%,rgba(255,255,255,0.15),transparent_25%)]"></div>

      {/* Floating orbs for dynamic background effect */}
      <div className="absolute top-10 left-10 w-32 h-32 bg-pink-400 rounded-full blur-3xl opacity-40 animate-pulse"></div>
      <div className="absolute bottom-20 right-20 w-48 h-48 bg-yellow-300 rounded-full blur-3xl opacity-30 animate-ping"></div>

      {/* Main content box */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        className="relative z-10 bg-white/10 backdrop-blur-2xl rounded-3xl p-10 shadow-2xl border border-white/20 text-center max-w-3xl"
      >
        <motion.h1
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 1 }}
          className="text-5xl md:text-6xl font-extrabold mb-4 bg-gradient-to-r from-yellow-300 via-pink-400 to-red-500 bg-clip-text text-transparent drop-shadow-lg"
        >
          Sri Matha Fancy Stores
        </motion.h1>

        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.8, duration: 1 }}
          className="text-xl md:text-2xl text-white/90 font-medium"
        >
          ✨ Our online store is launching soon — stay tuned! ✨
        </motion.p>

        <motion.div
          initial={{ scaleX: 0 }}
          animate={{ scaleX: 1 }}
          transition={{ delay: 1.2, duration: 1 }}
          className="w-32 h-1 bg-yellow-400 mx-auto mt-6 rounded-full origin-left"
        ></motion.div>

        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1.8, duration: 1 }}
          className="mt-8 text-sm text-white/70"
        >
          © 2025 Sri Matha Fancy Stores. All Rights Reserved.
        </motion.p>
      </motion.div>
    </div>
  );
};

export default App;
